public class Main_30 {
    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> list = new ArrayList<>();
        int step = words[0].length();
        int total = step * words.length;
        int i = 0;
        List<String> l = new ArrayList<>();
        p(words, 0, l);
        while (i <= s.length() - total) {
            String temp = s.substring(i, i + total);
            if (l.contains(temp)) {
                list.add(i);
            }
            i++;
        }
        return list;
    }

    public static void p(String[] a, int index, List<String> list) {
        if (a == null || index < 0)
            return;
        if (index == (a.length - 1)) {
            list.add(Arrays.toString(a));
        } else {
            for (int i = index; i < a.length; i++) {
                String temp = a[i];
                a[i] = a[index];
                a[index] = temp;
                p(a, index + 1, list);
                temp = a[i];
                a[i] = a[index];
                a[index] = temp;
            }
        }
    }

    public static void main(String[] args) {
        String s = "barfoothefoobarman";
        String[] words = {"foo", "bar"};
        System.out.println(findSubstring(s, words));

        String s0 = "";
        String[] words0 = {"dhvf", "sind", "ffsl", "yekr", "zwzq", "kpeo", "cila", "tfty", "modg", "ztjg", "ybty", "heqg", "cpwo", "gdcj", "lnle", "sefg", "vimw", "bxcb"};
//        System.out.println(findSubstring(s0, words0));

        List<String> list = new ArrayList<>();
        p(words0, 0, list);
        System.out.println(list);
    }
}
